    $SQL = "select DATE_FORMAT(datehired, '%Y') datehired  from extia order by datehired asc limit 1";
    $wpdb->query($SQL);
    $records =  $wpdb->get_results($SQL);
    $companyEpoch = $startyear = $records[0]->datehired;



    $SQL = "select DATE_FORMAT(datehired, '%Y') datehired  from extia order by datehired desc limit 1";
    $wpdb->query($SQL);
    $records =  $wpdb->get_results($SQL);
    $endyear = $records[0]->datehired;


//    $firstyear;
//    $firstyear->total = 1;
//    $firstyear->year = 1999;
//    $startyear = 1998;

//    $newHireData = array(0=>$firstyear);
    $newHireData = array();
    while ($startyear <= $endyear) {
        $plusone = $startyear +1 ;


        $SQL = "select count(id) newhires from extia where datehired between '$startyear-01-01 00:00:00' and '$plusone-01-01 00:00:00'";
        $wpdb->query($SQL);
        $rows =  $wpdb->get_results($SQL);
        $record = $rows[0];

        $data->newHires = (int)$record->newhires;
        $data->year = (int)$startyear;



        $SQL = "select count(id) total from extia where datehired between '$companyEpoch-01-01 00:00:00' and '$plusone-01-01 00:00:00'";
        
        $wpdb->query($SQL);
        $rows =  $wpdb->get_results($SQL);
        $record = $rows[0];

        $data->total = (int)$record->total;

        if ($startyear == 2000)  {
            $data->newHires-=1;
            $data->prevHired = 1;
        }
        else {
                $data->prevHired =  $record->total - $data->newHires;
                

        }




        // get department list
        $SQL = "select distinct department from extia order by department asc";

        $wpdb->query($SQL);
        $departments =  $wpdb->get_results($SQL);

        $departmentBreakdown = array();

        while (list($num, $record) = each ($departments)) {

            //Number of new hires
            $SQL = "select count(id) numHires from extia where  datehired between '$startyear-01-01 00:00:00' and '$plusone-01-01 00:00:00' and  department = '" . $record->department . "'";

            $wpdb->query($SQL);
            $rows =  $wpdb->get_results($SQL);
            $rows[0]->department = $record->department;
            $rows[0]->numHires = (int)$rows[0]->numHires;

            if ($startyear != 2000)  {

                // Total number of employees
                $SQL = "select count(id) totalEmployees from extia where datehired between '$companyEpoch-01-01 00:00:00' and '$plusone-01-01 00:00:00' and  department = '" . $record->department . "'";

                $wpdb->query($SQL);
                $totalEmps =  $wpdb->get_results($SQL);
                $rows[0]->totalEmployees = (int)$totalEmps[0]->totalEmployees;

            }


            // Salary
            $SQL = "select sum(salary) salary from extia where  datehired between '$startyear-01-01 00:00:00' and '$plusone-01-01 00:00:00' and  department = '" . $record->department . "'";

            $wpdb->query($SQL);
            $rowsx =  $wpdb->get_results($SQL);
            $rows[0]->totalSalary = (int)$rowsx[0]->salary;//"$" . number_format($rowsx[0]->salary);
            $rows[0]->avgSalary   = (int)($rowsx[0]->salary / $rows[0]->numHires);//"$" . number_format($rowsx[0]->salary / $rows[0]->numHires);

            $salaryRanges = array(20000,30000,40000,50000);

            // Get the salary ranges.
            $salaryData = array();
            while (list($num, $range) = each ($salaryRanges)) {

                $endRange = $range + 10000;
               // Salary
                $SQL = "select count(salary) total from extia where datehired between '$companyEpoch-01-01 00:00:00' and '$plusone-01-01 00:00:00' and  department = '" . $record->department . "' and salary between $range and $endRange";
                $wpdb->query($SQL);
                $r =  $wpdb->get_results($SQL);
                $r[0]->range = number_format($range) . "'s";
                $r[0]->department = $record->department;

                array_push($salaryData, $r[0]);

            }
            $rows[0]->salaryRanges = $salaryData;

            array_push($departmentBreakdown, $rows[0]);
        }


        $data->departmentBreakdown =  $departmentBreakdown ;





        array_push($newHireData, $data);

        $startyear++;
        unset ($data);
	}




	if ($callback) {
